import React from 'react';
import ReactDom from 'react-dom';
import './index.css';
import appleImg from './apple.png';
import store from './mobx/index'
import { observable } from 'mobx-react';
let storeData = new store()

@observable
class AppleBasket extends React.Component {
  proList() {
    console.log(this.props)
    let pros = storeData.getApples()?storeData.getApples():storeData.apples
    let proItem
    if(pros.length > 0){
      proItem = pros.map(item => 
        <div className='proList' key={item.id}>
          <img src={appleImg} alt='商品图片'></img>
          <div>
            <div>
            <span className='proName'>红苹果 _ {item.id} 号</span>
            </div>
            <div>
              {item.weight}克
            </div>
          </div>
          <button onClick={this.eatApple} data-id={item.id}>吃掉</button>
        </div>  
      )
    }else{
      proItem = <div className='proList'>
        暂无数据
      </div>
    }
    return proItem
  }
  // 摘苹果
insertApple(){
  storeData.apples.push({
    id: (storeData.apples.length+1),
    weight: Math.floor(Math.random() * (250 - 200 + 1) + 200),
    isEaten: false
  })
  storeData.setApples()
  storeData.getApples()
}
clearApple(){
  storeData.clearApples()
}
// 吃苹果
eatApple(props){
  console.log(props)
}
  render(){
    return (
      <div className="App">
      <header className="App-header">
        苹果篮子
      </header>
      {/* 合计数量重量 */}
      <div className='countBox'>
        <div className='left-count'>
          <div className='label'>当前</div>
          <div>{storeData.Total.curCount.count}个苹果，{storeData.Total.curCount.weight}克</div>
        </div>
        <div className='right-count'>
          <div className='label'>已吃掉</div>
          <div>{storeData.Total.eatCount.count}个苹果，{storeData.Total.eatCount.weight}克</div>
        </div>
      </div>
      {/* 商品列表 */}
      <div className='pros'>
        {this.proList()}
      </div>
      <div className='foot'>
        <button className='addBtn' onClick={this.insertApple}>摘苹果</button>
        <button className='clearBtn' onClick={this.clearApple}>清空</button>
      </div>
    </div>
    )
  }
}
ReactDom.render(
  <AppleBasket/>,
  document.getElementById('root')
)
