import { observable, computed } from 'mobx';
class appleStore {
    @observable apples = [];
    // 果子重量,取200 至250 之间的随机值
    @observable weight = Math.random()*(250-200+1)+200;
    // 摘果子 状态 控制按钮显示
    @observable isPick = true
    // 获取苹果的总计
    @computed get Total() {
        let options = {
            // 当前摘掉
            curCount: {
                count: 0,
                weight: 0
            },
            // 已吃掉
            eatCount: {
                count: 0,
                weight: 0
            }
        }

        // 判断苹果是否已被吃掉
        this.apples.forEach(apple => {
            let selector = apple.isEaten ? 'eatCount':'curCount';
            options[selector].count ++;
            options[selector].weight += apple.weight;
        });

        return options
    }
}
export default appleStore
