import appleImg from './apple.png';
import store from './mobx/index'
import './App.css';
let storeData = new store()
function ProList() {
  let pros = storeData.getApples()?storeData.getApples():storeData.apples
  let proItem
  if(pros.length > 0){
    proItem = pros.map(item => 
      <div className='proList' key={item.id}>
        <img src={appleImg} alt='商品图片'></img>
        <div>
          <div>
          <span className='proName'>红苹果 _ {item.id} 号</span>
          </div>
          <div>
            {item.weight}克
          </div>
        </div>
        <button onClick={eatApple} data-id={item.id}>吃掉</button>
      </div>  
    )
  }else{
    proItem = <div className='proList'>
      暂无数据
    </div>
  }
  return proItem
}
// 摘苹果
function insertApple(){
  storeData.apples.push({
    id: (storeData.apples.length+1),
    weight: Math.floor(Math.random() * (250 - 200 + 1) + 200),
    isEaten: false
  })
  storeData.setApples()
}
function clearApple(){
  storeData.clearApples()
}
// 吃苹果
function eatApple(props){
  console.log(props)
}
function App() {
  return (
    <div className="App">
      <header className="App-header">
        苹果篮子
      </header>
      {/* 合计数量重量 */}
      <div className='countBox'>
        <div className='left-count'>
          <div className='label'>当前</div>
          <div>{storeData.Total.curCount.count}个苹果，{storeData.Total.curCount.weight}克</div>
        </div>
        <div className='right-count'>
          <div className='label'>已吃掉</div>
          <div>{storeData.Total.eatCount.count}个苹果，{storeData.Total.eatCount.weight}克</div>
        </div>
      </div>
      {/* 商品列表 */}
      <div className='pros'>
        <ProList></ProList>
      </div>
      <div className='foot'>
        <button className='addBtn' onClick={insertApple}>摘苹果</button>
        <button className='clearBtn' onClick={clearApple}>清空</button>
      </div>
    </div>
  );
}

export default App;
