import React, { Component } from 'react';
import ReactDom from 'react-dom';
import './index.css';
import appleImg from './apple.png';
import store from './mobx/index'
import { inject, observer } from 'mobx-react';

store.getApples()
let pros = store.apples
//@inject('storeData')
@observer
class AppleItem extends Component {
  render(){
    console.log(this.props)
    let { apples } = this.props
    let proItem
    if(apples && apples.length > 0){
      proItem = apples.map(item => 
        <div className='proList' key={item.id}>
          <img src={appleImg} alt='商品图片'></img>
          <div>
            <div>
            <span className='proName'>红苹果 _ {item.id} 号</span>
            </div>
            <div>
              {item.weight}克
            </div>
          </div>
          <button onClick={this.eatApple} data-id={item.id}>吃掉</button>
        </div>  
      )
    }else{
      proItem = <div className='proList'>
        暂无数据
      </div>
    }
    return proItem
  }
}
@observer
class AppleBasket extends Component {
  constructor(props){
    super(props)
  }
  // 摘苹果
insertApple(){
  store.addApple({
    id: (pros.length+1),
    weight: Math.floor(Math.random() * (250 - 200 + 1) + 200),
    isEaten: false
  })
  store.setApples(pros)
  console.log(store)
}
clearApple(){
  store.clearApples()
}
// 吃苹果
eatApple(props){
  console.log(props)
}
  render(){
    return (
      <div className="App">
      <header className="App-header">
        苹果篮子
      </header>
      {/* 合计数量重量 */}
      <div className='countBox'>
        <div className='left-count'>
          <div className='label'>当前</div>
          <div>{store.Total.curCount.count}个苹果，{store.Total.curCount.weight}克</div>
        </div>
        <div className='right-count'>
          <div className='label'>已吃掉</div>
          <div>{store.Total.eatCount.count}个苹果，{store.Total.eatCount.weight}克</div>
        </div>
      </div>
      {/* 商品列表 */}
      <div className='pros'>
        <AppleItem apples={store.apples}></AppleItem>
      </div>
      <div className='foot'>
        <button className='addBtn' onClick={this.insertApple}>摘苹果</button>
        <button className='clearBtn' onClick={this.clearApple}>清空</button>
      </div>
    </div>
    )
  }
}
ReactDom.render(
  <React.StrictMode>
    <AppleBasket/>
  </React.StrictMode>,
  document.getElementById('root')
)
